    private StringBuilder ExtractFamilyAdults(Family family)
    {
        StringBuilder trmp = new StringBuilder();
        int count = 0;
        foreach (var adult in family.Adults)
        {
            if (adult != null)
            {
                count++;
                trmp.Append(adult.FirstName + " " + adult.LastName + ", ");
            }
        }

        if (count != 0)
        {
            trmp.Length--;
        }
        return trmp;
    }
    
